var orderStripeSubscriptionDetails={template:`<div class="order-event"><div class="order-event-icon" :class="data.class || 'vx-neutral'"><icon-checkmark v-if="['active','trialing'].includes(data.status)"/><icon-info v-else/></div><b>{{ data.long_label }}</b> <span>{{ data.message }}</span><div v-if="actions.length" class="further-actions"><template v-for="action in actions"><a href="#" @click.prevent="parent.runAction(action)" class="ts-btn ts-btn-1">{{ action.label }}</a></template></div></div>`,props:{parent:Object,order:Object,data:Object},computed:{actions(){if(!Array.isArray(this.data.actions))return[];let a=[];return this.data.actions.forEach(t=>{t=this.parent.getAction(t);null!==t&&a.push(t)}),a}}};export{orderStripeSubscriptionDetails as default};
